            public void SendAndReceive(StringTree parameterizedLogEntry, out DateTime sentAt, out TimeSpan timeToLastByte, out Exception exception) {
                _parameterizedLogEntry = parameterizedLogEntry;
                exception = null;
                
                //
                // parameterizedLogEntry is the log entry parsed to a String Tree using the Log Rule Set.
                // String Tree is a simple class: it can have either a value (property: string Value) or childs who are also String Trees (e.g. StringTree foo = parameterizedLogEntry[n] where n is an integer value).
                // You can get the combined value of the childs using the function CombineValues() (not shallow, returns a string), this uses the child delimiters in the rule set to glue these values together.
                //
                
                //
                // Initiate other stuff here.
                // (eg. Fields or functions from the free coding section.)
                //
                
                if (_isDisposed) {
                    sentAt = DateTime.Now;
                    timeToLastByte = new TimeSpan();
                    return;
                }
                try {
                    sentAt = DateTime.Now;
                    _stopwatch.Start();
                    try {
                        //
                        // Send the request to the server here.
                        //
                        
                        InitHttpWebRequest();
                        InitCookies();
                        HandleCookiesBeforeRequest();
                        ApplyPostData();
                        _httpWebResponse = _httpWebRequest.GetResponse() as HttpWebResponse;
                        
                        if (_httpWebResponse.StatusCode == HttpStatusCode.NotFound) {
                            throw new Exception("Page not found!");
                        }
                        
                        if (_httpWebResponse.ContentEncoding.ToLower().Contains("gzip")) {
                            _streamReader = new StreamReader(new GZipStream(_httpWebResponse.GetResponseStream(), CompressionMode.Decompress));
                        }
                        else if(_httpWebResponse.ContentEncoding.ToLower().Contains("deflate")){
                            _streamReader = new StreamReader(new DeflateStream(_httpWebResponse.GetResponseStream(), CompressionMode.Decompress));
                        }
                        else {
                            _streamReader = new StreamReader(_httpWebResponse.GetResponseStream(), (_httpWebResponse.ContentEncoding.Length != 0) ?
                            System.Text.Encoding.GetEncoding(_httpWebResponse.ContentEncoding) : System.Text.Encoding.GetEncoding(1252));
                        }
                        
                        
                        //
                        // CUSTOMIZATION:
                        //    You can put your own code for extracting data from the response here. When making changes, don't forget to invoke DownloadResponse().
                        //    Before making any changes, please understand the workings of the Connection Proxy or ask help.
                        //
                        
                        #if USEVIEWSTATE
                            string relUrl = _parameterizedLogEntry[_relativeUrl].Value;
                            string method = _parameterizedLogEntry[_requestMethod].Value;
                            
                            if (_viewstate_urls.Contains(relUrl) && method == "GET") {
                                string response = DownloadResponse(false); // Get full response in memory
                                _viewstate = HttpUtility.UrlEncode(response.GetStringBetween("<input type=\"hidden\" name=\"__VIEWSTATE\" id=\"__VIEWSTATE\" value=\"", "\" />"));
                                _eventvalidation = HttpUtility.UrlEncode(response.GetStringBetween("<input type=\"hidden\" name=\"__EVENTVALIDATION\" id=\"__EVENTVALIDATION\" value=\"", "\" />"));
                            }
                            else {
                                DownloadResponse(); // Discard response while it gets downloaded
                            }
                            
                            #if DEBUG
                                EventPanel.AddEvent("USEVIEWSTATE"
                                + "\n  relUrl: " + relUrl
                                + "\n  method: " + method
                                + "\n  viewstate: " + _viewstate
                                + "\n  eventvalidation: " + _eventvalidation;
                            #endif
                            
                            #elif USEDRUPAL
                            string relUrl = _parameterizedLogEntry[_relativeUrl].Value;
                            string method = _parameterizedLogEntry[_requestMethod].Value;
                            string location = _httpWebResponse.GetResponseHeader("Location");
                            
                            if (_formtoken_urls.Contains(relUrl) && method == "GET") {
                                string response = DownloadResponse(false); // Get full response in memory
                                _formId = response.GetStringBetween("<input type=\"hidden\" name=\"form_build_id\" value=\"", "\" />");
                                _formToken = response.GetStringBetween("<input type=\"hidden\" name=\"form_token\" value=\"", "\" />");
                            }
                            else {
                                DownloadResponse(); // Discard response while it gets downloaded
                            }
                            
                            if (_redirect_urls.Contains(relUrl) && !string.IsNullOrEmpty(location)) {
                                _redirectNode = location.GetStringBetween("/node/", "?render=overlay");
                            }
                            
                            #if DEBUG
                                EventPanel.AddEvent("USEDRUPAL"
                                + "\n  relUrl: " + relUrl
                                + "\n  method: " + method
                                + "\n  location: " + location
                                + "\n  formId: " + _formId
                                + "\n  formToken: " + _formToken
                                + "\n  redirectnode: " + _redirectNode);
                            #endif
                            
                            #elif USECHAMILO
                            string relUrl = _parameterizedLogEntry[_relativeUrl].Value;
                            string method = _parameterizedLogEntry[_requestMethod].Value;
                            string getData = _parameterizedLogEntry[_getData].CombineValues();
                            string location = _httpWebResponse.GetResponseHeader("Location");
                            
                            DownloadResponse(); // Discard response while it gets downloaded
                            
                            // Here the _calendar_search is used to try and extract the _repo_object from the response's location header.
                            if (relUrl.StartsWith("/chamilo/index.php") && getData == _calendar_search) {
                                if (! string.IsNullOrEmpty(location)) {
                                    var query = HttpUtility.ParseQueryString(location);
                                    _repo_object = query["repo_object"];
                                    
                                    #if DEBUG
                                        EventPanel.AddEvent("USECHAMILO"
                                        + "\n  relUrl: " + relUrl
                                        + "\n  method: " + method
                                        + "\n  getData: " + getData
                                        + "\n  location: " + location
                                        + "\n  repoObject: " + _repo_object);
                                    #endif
                                }
                            }
                            
                            #else
                            DownloadResponse(); // Discard response while it gets downloaded
                            
                        #endif
                        
                        HandleCookiesAfterRequest();
                        
                        //
                        // CUSTOMIZATION:
                        //    Put your code for cookie data extraction here.
                        //
                        
                        #if USEPHPBB
                            if (_parameterizedLogEntry[_relativeUrl].Value == "/index.php" || _parameterizedLogEntry[_relativeUrl].Value == "/ucp.php") {
                                _sid = _cookieCollection["phpbb3_ek7cb_sid"].Value;
                                
                                #if DEBUG
                                    EventPanel.AddEvent("USEPHPBB"
                                    + "\n _sid: " + _sid);
                                #endif
                            }
                        #endif
                    }
                    catch (Exception ex) {
                        exception = ex;
                        #if DEBUG
                            EventPanel.AddEvent("SendAndReceive Exception: " + exception.Message);
                        #endif
                    }
                    finally {
                        //
                        // Close stuff if needed.
                        //
                        
                        if (_streamReader != null) {
                            try {
                                _streamReader.Close();
                                _streamReader.Dispose();
                            }
                            finally {
                                _streamReader = null;
                            }
                        }
                    }
                }
                catch {
                    //
                    // Always throw the exception, if any, if 'stuff' cannot be closed.
                    // vApus will handle it as a connection problem.
                    //
                    
                    throw;
                }
                finally {
                    _stopwatch.Stop();
                    timeToLastByte = _stopwatch.Elapsed;
                    _stopwatch.Reset();
                }
            }
            
            public void Dispose() {
                if (!_isDisposed) {
                    _isDisposed = true;
                    try {
                        // Timeout immediately.
                        _httpWebRequest.Abort();
                        } catch {}
                        _httpWebRequest = null;
                        _httpWebResponse = null;
                        _cookieCollection= null;
                        _cookieDecoder = null;
                        if (_streamReader != null) {
                            try {
                                _streamReader.Close();
                                _streamReader.Dispose();
                                _streamReader = null;
                                } catch {}
                            }
                        }
                    }
                #endregion // Functions
                
                #region Free Coding
                    private void InitHttpWebRequest() {
                        string relUrl = _parameterizedLogEntry[_relativeUrl].Value;
                        string getData = _parameterizedLogEntry[_getData].CombineValues();
                        
                        // RelUrl changes.
                        #if USEDRUPAL
                            if (relUrl == "/node/@replaceNode" && _redirectNode != string.Empty) {
                                relUrl = relUrl.Replace("@replaceNode", _redirectNode);
                            }
                        #endif
                        
                        // GetData changes.
                        #if USECHAMILO
                            if(getData.Length != 0) {
                                getData = "?" + getData.Replace("@repo_object", _repo_object);
                            }
                            #elif USEPHPBB
                            if(getData.Length != 0) {
                                getData = "?" + getData.Replace("@replaceSID", _sid);
                            }
                            #else
                            if(getData.Length != 0) {
                                getData = "?" + getData;
                            }
                        #endif
                        
                        _httpWebRequest = (HttpWebRequest)WebRequest.Create(new Uri(string.Format("{0}://{1}:{2}{3}{4}",
                        _parameterizedLogEntry[_protocol].Value, getDestinationHost(), _parameterizedLogEntry[_destinationPort].Value,
                        relUrl, getData)));
                        
                        // Set the HTTP request method (GET/POST).
                        _httpWebRequest.Method = _parameterizedLogEntry[_requestMethod].Value;
                        // Set the Accept header.
                        _httpWebRequest.Accept = _parameterizedLogEntry[_accept].Value;
                        // Set the Referer header.
                        #if USEREFERER
                            _httpWebRequest.Referer = _parameterizedLogEntry[_referer].Value;
                        #endif
                        // A 60 second timeout should be reasonable.
                        _httpWebRequest.Timeout = 60000;
                        _httpWebRequest.ReadWriteTimeout = _httpWebRequest.Timeout;
                        // Allow keepalive.
                        _httpWebRequest.KeepAlive = true;
                        // Allow compression.
                        _httpWebRequest.Headers.Add("Accept-Encoding: gzip, deflate");
                        // Set the Accept-Language header.
                        _httpWebRequest.Headers.Add("Accept-Language: en-US");
                        // Don't autoredirect: redirects are already logged.
                        _httpWebRequest.AllowAutoRedirect = false;
                        // Set the initial content length to 0.
                        _httpWebRequest.ContentLength = 0;
                        // Fancy stuff.
                        _httpWebRequest.ServicePoint.Expect100Continue = false;
                        _httpWebRequest.ServicePoint.ConnectionLimit = (this.GetParent() as ConnectionProxyPool).PoolSize;
                        
                        #if DEBUG
                            _httpWebRequest.Headers.Add("vApus-User: " + System.Threading.Thread.CurrentThread.Name);
                        #endif
                        
                        // EXPERIMENTAL - Allways set KeepAlive header //
                        //_httpWebRequest.ServicePoint.GetType().GetProperty("HttpBehaviour", BindingFlags.Instance | BindingFlags.NonPublic).SetValue(_httpWebRequest.ServicePoint, (byte)0, null);
                        
                        #if USECUSTOMHEADERS
                            foreach (string header in _custom_headers) {
                                _httpWebRequest.Headers.Add(header);
                            }
                        #endif
                    }
                    
                    private void InitCookies() {
                        if (_cookieCollection == null) {
                            _httpWebRequest.CookieContainer = new CookieContainer();
                            _cookieCollection = new CookieCollection();
                            _cookieDecoder = new CookieDecoder();
                        }
                        
                        #if USELOGGEDCOOKIES
                            foreach (var kvp in _parameterizedLogEntry[_cookies]) {
                                string key = kvp[0].Value.TrimStart();
                                string value = ((kvp.Count == 1) ? string.Empty : kvp[1].Value);
                                Cookie cookie = new Cookie(key, value);
                                
                                int cookieIndex = GetCookieIndex(cookie, _cookieCollection);
                                if (cookieIndex == -1) {
                                    cookie.Domain = getDestinationHost();
                                    _cookieCollection.Add(cookie);
                                }
                            }
                        #endif
                    }
                    
                    private void ApplyPostData() {
                        if (_parameterizedLogEntry[_postData].Value.Length != 0) {
                            string postData = _parameterizedLogEntry[_postData].Value;
                            
                            #if USEVIEWSTATE
                                if (_viewstate_urls.Contains(_parameterizedLogEntry[_relativeUrl].Value) && _viewstate != string.Empty) {
                                    postData = postData.Replace("@replaceViewstate", _viewstate).Replace("@replaceEventvalidation", _eventvalidation);
                                }
                                
                                #elif USEDRUPAL
                                if (_formtoken_urls.Contains(_parameterizedLogEntry[_relativeUrl].Value) && _formId != string.Empty && _formToken != string.Empty) {
                                    postData = postData.Replace("@replaceFormId", _formId).Replace("@replaceFormToken", _formToken);
                                }
                                
                                #elif USECHAMILO
                                if (_repo_object != string.Empty) {
                                    postData = postData.Replace("@repo_object_length", _repo_object.Length.ToString()).Replace("@repo_object", _repo_object);
                                }
                                
                                // Some custom headers are added when _document_search match is found.
                                if (_parameterizedLogEntry[_getData].CombineValues() == _document_search) {
                                    _httpWebRequest.Headers.Add("Pragma", "no-cache");
                                }
                                
                                #elif USEPHPBB
                                if (_sid != string.Empty) {
                                    postData = postData.Replace("@replaceSID", _sid);
                                }
                                
                                #else
                                
                            #endif
                            
                            _httpWebRequest.ContentLength = postData.Length;
                            _httpWebRequest.ContentType = String.IsNullOrEmpty(_parameterizedLogEntry[_contentType].Value) ? "application/x-www-form-urlencoded" : _parameterizedLogEntry[_contentType].Value;
                            Stream postStream = _httpWebRequest.GetRequestStream();
                            StreamWriter postStreamWriter = new StreamWriter(postStream);
                            postStreamWriter.Write(postData);
                            postStreamWriter.Flush();
                            
                            postStreamWriter.Close();
                            postStreamWriter.Dispose();
                            postStreamWriter = null;
                            
                            postStream.Close();
                        }
                    }
                    
                    private void HandleCookiesBeforeRequest() {
                        #if USECUSTOMCOOKIEPARSER
                            //Domain for the cookies should be determined here.
                            string domain = _httpWebRequest.RequestUri.GetLeftPart(UriPartial.Authority);
                            if(domain.StartsWith("http://")) {
                                domain = domain.Substring(7);
                            }
                            else if(domain.StartsWith("https://")) {
                                domain = domain.Substring(8);
                            }
                            
                            // Support for top level domains; we probably don't need this.
                            // TODO: Should be tested with a URL (not IP) to verify this.
                            /*
                                IPAddress ipAddress;
                                if(!IPAddress.TryParse(authority, out ipAddress)) {
                                    int dotIndex = authority.IndexOf('.');
                                    if (dotIndex != -1 && dotIndex != authority.Length - 1) {
                                        domain = authority.Substring(dotIndex + 1);
                                    }
                                }
                            */
                            
                            foreach (Cookie cookie in _cookieCollection) {
                                cookie.Domain = domain;
                            }
                        #endif
                        
                        _httpWebRequest.CookieContainer = new CookieContainer();
                        _httpWebRequest.CookieContainer.Add(_cookieCollection);
                    }
                    
                    private void HandleCookiesAfterRequest() {
                        #if USECUSTOMCOOKIEPARSER
                            var newCookieCollection = GetCookies(_httpWebResponse.Headers);
                            var requestCookieCollection = GetCookies(_httpWebRequest.Headers);
                            #else
                            var newCookieCollection = _httpWebResponse.Cookies;
                            var requestCookieCollection = _httpWebRequest.CookieContainer.GetCookies(_httpWebRequest.RequestUri);
                        #endif
                        
                        // Add ASP authentication cookies and old cookies that might be important (better sending too many cookies than too few).
                        foreach (Cookie cookie in requestCookieCollection) {
                            if (GetCookieIndex(cookie, newCookieCollection) == -1) {
                                newCookieCollection.Add(cookie);
                            }
                        }
                        // Copy the cached cookies that are not yet in the collection, the check happens on the name of the cookie.
                        foreach (Cookie cookie in _cookieCollection) {
                            if (GetCookieIndex(cookie, newCookieCollection) == -1) {
                                newCookieCollection.Add(cookie);
                            }
                        }
                        _cookieCollection = newCookieCollection;
                    }
                    
                    private int GetCookieIndex(Cookie cookie, CookieCollection cookieCollection) {
                        for (int i = 0; i < cookieCollection.Count; i++) {
                            if (cookieCollection[i].Name == cookie.Name) {
                                return i;
                            }
                        }
                        return -1;
                    }
                    
                    private CookieCollection GetCookies(WebHeaderCollection headers) {
                        var cookieCollection = new CookieCollection();
                        foreach (string headerName in headers.Keys) {
                            string lowerheaderName = headerName.ToLower();
                            if (lowerheaderName == "set-cookie" || lowerheaderName == "cookie") {
                                foreach (Cookie cookie in _cookieDecoder.Decode(headers[headerName])) {
                                    int cookieIndex = GetCookieIndex(cookie, cookieCollection);
                                    if (cookieIndex == -1) {
                                        cookieCollection.Add(cookie);
                                    }
                                    else {
                                        cookieCollection[cookieIndex].Value = cookie.Value;
                                    }
                                }
                            }
                        }
                        return cookieCollection;
                    }
                    
                    private string getDestinationHost() {
                        #if USEMULTICONNECTION
                            return _parameterizedLogEntry[_destinationHost].Value;
                            #else
                            return _connectionProxySyntaxItem0;
                        #endif
                    }
                    
                    //private string getStringBetween(string s, string before, string after) {
                        //s = s.Substring(s.IndexOf(before, StringComparison.InvariantCultureIgnoreCase) + before.Length);
                        //int endIndex = s.IndexOf(after, StringComparison.InvariantCultureIgnoreCase);
                        //return s.Substring(0, endIndex);
                    //}
                    
                    // Downloads the full response and returns the page.
                    // If discard is set (default), the response will be buffered in small blocks and discarded.
                    // If we want to keep the full response for parsing, set discard = false.
                    private string DownloadResponse(bool discard = true) {
                        if (discard) {
                            do {
                                _count = _streamReader.Read(_buffer, 0, 1024);
                            } while (_count != 0);
                        }
                        else {
                            return _streamReader.ReadToEnd();
                        }
                        
                        return string.Empty;
                    }
                #endregion // Free Coding
            } // ConnectionProxy
            
            public static class StringExtensions {
                public  static string GetStringBetween(this string s, string before, string after) {
                    s = s.Substring(s.IndexOf(before, StringComparison.InvariantCultureIgnoreCase) + before.Length);
                    int endIndex = s.IndexOf(after, StringComparison.InvariantCultureIgnoreCase);
                    return s.Substring(0, endIndex);
                }
            } // StringExtensions
            
        } // vApus.Stresstest
