            public void Dispose() {
                if (!_isDisposed) {
                    _isDisposed = true;
                    try {
                        // Timeout immediately.
                        _httpWebRequest.Timeout = 1;
                        _httpWebRequest.ReadWriteTimeout = _httpWebRequest.Timeout;
                        }catch{
                    }
                    _httpWebRequest = null;
                    _httpWebResponse = null;
                    _cookieCollection= null;
                    _cookieDecoder = null;
                    if (_streamReader != null) {
                        try {
                            _streamReader.Close();
                            _streamReader.Dispose();
                            _streamReader = null;
                        }
                        catch {
                        }
                    }
                }
